  NO CASCADE BEFORE INSERT
  ON "DABS"."CMTANNOUNCEMENT_T"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC
	IF (NEW.RECORD_STATUS='ACTIVE') THEN 
		SET NEW.CHECK_CONDITION = NEW.JUNCTION||','||TRUNC(NEW.EFFECT_DATE);--
	ELSE 
		SET NEW.CHECK_CONDITION = NEW.ID;--
	END IF;--
END ;
