  ON "DABS"."CONSUMERS_HHC_DOWNLOAD"
  REFERENCING 
    NEW AS NEW
  FOR EACH ROW
BEGIN ATOMIC
        DECLARE CNT INTEGER;--
        IF (NEW.RECORD_STATUS='ACTIVE') THEN 
                SET NEW.CHECK_CONDITION = NEW.CONS_NUMBER||','|| NEW.DHARI_YEAR ||','|| NEW.CYCLE;--
        ELSE 
               SET NEW.CHECK_CONDITION =NEW.ID;--
         END IF;--
END ;
