CREATE TRIGGER "DABS"."TRIG_BRIN_DEPARTMENT_M"
  NO CASCADE BEFORE INSERT
  ON "DABS"."DEPARTMENT_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.DEPT_NAME;--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;
CREATE TRIGGER "DABS"."TRIG_BRUP_DEPARTMENT_M"
  NO CASCADE BEFORE UPDATE
  ON "DABS"."DEPARTMENT_M"
  REFERENCING 
              OLD AS "OLD"
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

	DECLARE CNT INTEGER;--

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.DEPT_NAME;--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;

