  NO CASCADE BEFORE INSERT
  ON "DABS"."ETISALAT_ACCOUNT_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC
	IF (NEW.RECORD_STATUS='ACTIVE') THEN 
		SET NEW.CHECK_CONDITION = NEW.ETISALAT_NO;--
	ELSE 
		SET NEW.CHECK_CONDITION = NEW.ID;--
	END IF;--
END ;
