CREATE TRIGGER "DABS"."TRIG_BRIN_HOLIDAY_MASTER"
  NO CASCADE BEFORE INSERT
  ON "DABS"."HOLIDAY_MASTER"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.TYPE||','||NEW.OCCASION||','||TO_CHAR(NEW.FROM_DATE,'DD-MON-YYYY')||','||TO_CHAR(NEW.TO_DATE,'DD-MON-YYYY');--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;
