  NO CASCADE BEFORE INSERT
  ON "DABS"."LTOBSERVATION_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

        IF (NEW.RECORD_STATUS='ACTIVE') THEN 

                SET NEW.CHECK_CONDITION = NEW.OBSERVATION;--

        ELSE 

                SET NEW.CHECK_CONDITION = NEW.ID;--

        END IF;--

END ;
CREATE TRIGGER "DABS"."TRIG_BRUP_LTOBSERVATION_M"
  NO CASCADE BEFORE UPDATE
  ON "DABS"."LTOBSERVATION_M"
  REFERENCING 
              OLD AS "OLD"
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

        DECLARE CNT INTEGER;--

        IF (NEW.RECORD_STATUS='ACTIVE') THEN 

                SET NEW.CHECK_CONDITION = NEW.OBSERVATION;--

        ELSE 

                SET NEW.CHECK_CONDITION = NEW.ID;--

        END IF;--

END ;

