CREATE VIEW DABS.SUMM_OFFWISE      ( NAME, OFFICE_ID, COLLECTION_DATE, CONSUMER_ID,
CASH_NOS,      CASH_AMT, CHEQUE_NOS, CHEQUE_AMT, DD_NOS, DD_AMT, CREDITCARD_NOS,
CREDITCARD_AMT, TCHEQUE_NOS, TCHEQUE_AMT )  AS  SELECT D.NAME,PD.OFFICE_ID,PM.COLLECTION_DATE,PD.CONSUMER_ID,
SUM(DECODE(LM.PAYMENT_MODE,'Cash',1,0)) CASH_NOS, SUM(DECODE(LM.PAYMENT_MODE,'Cash',(NVL(PD.AMOUNT_PAID,0)+NVL(PD.ADVANCE_AMT,0)),0))
AS CASH_AMT,  SUM(DECODE(LM.PAYMENT_MODE,'Cheque',1,0)) CHEQUE_NOS, SUM(DECODE(LM.PAYMENT_MODE,'Cheque',(NVL(PD.AMOUNT_PAID,0)+NVL(PD.ADVANCE_AMT,0)),0))
AS CHEQUE_AMT,  SUM(DECODE(LM.PAYMENT_MODE,'Demand Draft',1,0)) DD_NOS,
SUM(DECODE(LM.PAYMENT_MODE,'Demand Draft',(NVL(PD.AMOUNT_PAID,0)+NVL(PD.ADVANCE_AMT,0)),0))
AS DD_AMT,  SUM(DECODE(LM.PAYMENT_MODE,'Credit Card',1,0)) CREDITCARD_NOS,
SUM(DECODE(LM.PAYMENT_MODE,'Credit Card',(NVL(PD.AMOUNT_PAID,0)+NVL(PD.ADVANCE_AMT,0)),0))
AS CREDITCARD_AMT,  SUM(DECODE(LM.PAYMENT_MODE,'Treasury Cheque',1,0))
TCHEQUE_NOS, SUM(DECODE(LM.PAYMENT_MODE,'Treasury Cheque',(NVL(PD.AMOUNT_PAID,0)+NVL(PD.ADVANCE_AMT,0)),0))
AS TCHEQUE_AMT          FROM PAYMENT_MASTER_T PM,PAYMENT_DETAILS_T PD,CMTOFFICE_M
D  ,LTPAYMENT_MODE_M LM          WHERE PM.ID=PD.PAYMENT_ID  AND PM.PAYMENT_MODE_ID
= LM.ID  AND PD.OFFICE_ID=D.ID  AND PM.RECORD_STATUS='ACTIVE'  AND PD.RECORD_STATUS='ACTIVE'
AND D.RECORD_STATUS='ACTIVE'  GROUP BY D.NAME,PD.OFFICE_ID,PM.COLLECTION_DATE,PD.CONSUMER_ID
WITH NO ROW MOVEMENT ;
CREATE VIEW CENTRAL_ALL.SUMM_OFFWISE      ( NAME, OFFICE_ID,  COLLECTION_DATE,
CONSUMER_ID, CASH_NOS,      CASH_AMT, CHEQUE_NOS, CHEQUE_AMT,  DD_NOS,
DD_AMT,      CREDITCARD_NOS, CREDITCARD_AMT, TCHEQUE_NOS, TCHEQUE_AMT 
)  AS  SELECT D.NAME,PD.OFFICE_ID,PM.COLLECTION_DATE,PD.CONSUMER_ID, SUM(DECODE(LM.PAYMENT_MODE,'Cash',1,0))
 CASH_NOS, SUM(DECODE(LM.PAYMENT_MODE,'Cash',(NVL(PD.AMOUNT_PAID,0)+NVL(PD.ADVANCE_AMT,0)),0))
