CREATE TRIGGER "DABS"."TRIG_BRIN_LTPAYMENT_MODE_M"
  NO CASCADE BEFORE INSERT
  ON "DABS"."LTPAYMENT_MODE_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.PAYMENT_MODE;--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;
