  ON "DABS"."LTVOLTAGE_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC
	IF (NEW.RECORD_STATUS='ACTIVE') THEN 
		SET NEW.CHECK_CONDITION = NEW.VOLT_DESC||','||NEW.VOLT_GROUP||','||NEW.VOLT_UNIT;--
	ELSE 
		SET NEW.CHECK_CONDITION = NEW.ID;--
	END IF;--
END ;
