CREATE TRIGGER DABS.TRIG_BRIN_MOB_CONSUMPTION_HISTORY
  NO CASCADE BEFORE INSERT
  ON DABS.MOB_CONSUMPTION_HISTORY
  REFERENCING 
    NEW AS NEW
  FOR EACH ROW
BEGIN ATOMIC
	DECLARE CNT INTEGER;--
	IF (NEW.RECORD_STATUS='ACTIVE') THEN 
		SET CNT = (SELECT COUNT(*) FROM  DABS.MOB_CONSUMPTION_HISTORY WHERE RECORD_STATUS='ACTIVE'
               AND CONS_NUM = NEW.CONS_NUM AND BILLNUMBER = NEW.BILLNUMBER 
               AND UPPER(TRANSACTION_CODE) = UPPER(NEW.TRANSACTION_CODE) ) ;--
		IF (CNT>0) THEN 
			 SIGNAL SQLSTATE '80560' ('Already Record Exists...');--
		END IF;--
	END IF;--
END ;
