  ON "DABS"."MTR_MAKEM"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

        IF (NEW.RECORD_STATUS='ACTIVE') THEN 

                SET NEW.CHECK_CONDITION = NEW.MAKE;--

        ELSE 

                SET NEW.CHECK_CONDITION = NEW.ID;--

        END IF;--

END ;
