              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

        IF (NEW.RECORD_STATUS='ACTIVE') THEN 

                SET NEW.CHECK_CONDITION = TO_CHAR(NEW.START_DATE,'DD-MM-YYYY')||','||TO_CHAR(NEW.END_DATE,'DD-MM-YYYY')||','|| NEW.MR_ID;--

        ELSE 

