CREATE TRIGGER "DABS"."TRIG_BRIN_NCTDOCUMENT_M"
  NO CASCADE BEFORE INSERT
  ON "DABS"."NCTDOCUMENT_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.NAME;--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;
CREATE TRIGGER "DABS"."TRIG_BRUP_NCTDOCUMENT_M"
  NO CASCADE BEFORE UPDATE
  ON "DABS"."NCTDOCUMENT_M"
  REFERENCING 
              OLD AS "OLD"
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC
	DECLARE CNT INTEGER;--
	IF (NEW.RECORD_STATUS='ACTIVE') THEN 
		SET NEW.CHECK_CONDITION = NEW.NAME;--
	ELSE 
		SET CNT = (SELECT COUNT(*) FROM NCTSUBMITTED_DOCS_T WHERE DOCUMENT_ID = OLD.ID AND RECORD_STATUS='ACTIVE') ;--
		IF (CNT>0) THEN 
			 SIGNAL SQLSTATE '80560' ('Child Record Exists You Can''t Delete Record...');--
		END IF;--
		SET NEW.CHECK_CONDITION = NEW.ID;--
	END IF;--
END ;

