  FOR EACH ROW
BEGIN ATOMIC

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.TYPE;--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;
CREATE TRIGGER "DABS"."TRIG_BRUP_SGTOBJECTTYPE_M"
  NO CASCADE BEFORE UPDATE
  ON "DABS"."SGTOBJECTTYPE_M"
  REFERENCING 
              OLD AS "OLD"
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC
	DECLARE CNT INTEGER;--
	IF (NEW.RECORD_STATUS='ACTIVE') THEN 
		SET NEW.CHECK_CONDITION = NEW.TYPE;--
	ELSE 
		SET CNT = (SELECT COUNT(*) FROM SGTOBJECT_M WHERE OBJECTTYPE_ID = OLD.ID AND RECORD_STATUS='ACTIVE') ;--
		IF (CNT>0) THEN 
			 SIGNAL SQLSTATE '80560' ('Child Record Exists You Can''t Delete Record...');--
		END IF;--
		SET NEW.CHECK_CONDITION = NEW.ID;--
	END IF;--
END ;

