  NO CASCADE BEFORE INSERT
  ON "DABS"."WIRE_DETAILS_M"
  REFERENCING 
              NEW AS "NEW"
  FOR EACH ROW
BEGIN ATOMIC

	IF (NEW.RECORD_STATUS='ACTIVE') THEN 

		SET NEW.CHECK_CONDITION = NEW.TYPE_OF_WIRE;--

	ELSE 

		SET NEW.CHECK_CONDITION = NEW.ID;--

	END IF;--

END ;
