CREATE VIEW LOGACT_REPORT AS   SELECT COUNT(CREATED_BY) AS TOTAL_NO_OF_ACTIVITIES,(SELECT
 NVL(FIRST_NAME,'')||NVL(MIDDLE_NAME,'')||NVL(LAST_NAME,'') FROM CMTEMPLOYEE_M
 WHERE USER_ID=A.CREATED_BY) AS CREATED_BY,  (SELECT LOGIN_ID FROM CMTUSER_M
 WHERE ID = A.CREATED_BY ) AS LOGIN_ID,  (SELECT NAME FROM SGTROLE_M WHERE
 ID = (SELECT ROLE_ID FROM SGTROLEUSERREL_M WHERE USER_ID=A.CREATED_BY))
 AS ROLE,  TRUNC(CREATE_DATE) AS CREATE_DATE,MIN(CREATE_DATE) AS MIN_TIME_OF_THE_DAY,MAX(CREATE_DATE)
 AS MAX_TIME_OF_THE_DAY, 'RDG ENRY' AS ACTIVITY   FROM LTMTR_RDG_III_T
A    WHERE   TRUNC(CREATE_DATE) BETWEEN TRUNC(SYSDATE-7) AND TRUNC(SYSDATE)
  AND RECORD_STATUS='ACTIVE'  GROUP BY CREATED_BY,TRUNC(CREATE_DATE)  
  UNION ALL   SELECT COUNT(UPDATED_BY) TOTAL_NO_OF_ACTIVITIES,(SELECT NVL(FIRST_NAME,'')||NVL(MIDDLE_NAME,'')||NVL(LAST_NAME,'')
 FROM CMTEMPLOYEE_M WHERE USER_ID=A.UPDATED_BY) CREATED_BY,  (SELECT LOGIN_ID
 FROM CMTUSER_M WHERE ID = A.UPDATED_BY )AS LOGIN_ID,  (SELECT NAME FROM
 SGTROLE_M WHERE ID = (SELECT ROLE_ID FROM SGTROLEUSERREL_M WHERE USER_ID=A.UPDATED_BY))
 AS ROLE,  TRUNC(UPDATE_DATE) AS CREATE_DATE,MIN(CREATE_DATE) AS MIN_TIME_OF_THE_DAY,MAX(CREATE_DATE)
