    FROM SGTROLEUSERREL_M A,SGTROLEOBJECTREL_M B,  SGTOBJECT_M C,SGTOBJECTTYPE_M
D,SGTPRIVILEGE_M E,SGTOBJECTREL_M F             WHERE D.ID=C.OBJECTTYPE_ID
AND F.OBJECT_ID = B.OBJECT_ID AND C.ID = B.OBJECT_ID  AND E.ID=B.PRIVILEGE_ID
AND B.ROLE_ID = A.ROLE_ID  AND A.RECORD_STATUS='ACTIVE' AND C.RECORD_STATUS='ACTIVE'
AND E.RECORD_STATUS='ACTIVE' AND D.RECORD_STATUS='ACTIVE'  AND B.RECORD_STATUS='ACTIVE'
AND F.RECORD_STATUS='ACTIVE'  WITH NO ROW MOVEMENT ;
