CREATE VIEW DABS.RBPVDATA      ( USER_ID, ROLE_ID, OBJECT_ID,
OBJECT_NAME, PRIV_NAME,      OBJ_TYPE, PARENT_OBJ_ID, CREATED_BY, CREATE_DATE,
UPDATED_BY,      UPDATE_DATE, RECORD_STATUS, MONTH, YEAR, DB_IP_ADDRESS,
     SOURCE_OFFICE_ID, TARGET_OFFICE_ID, SCREEN_ID )  AS  SELECT A.USER_ID
USER_ID,A.ROLE_ID ROLE_ID,C.ID AS OBJECT_ID,  C.NAME AS OBJECT_NAME,E.NAME
AS PRIVILEGE_NAME,  D.TYPE OBJ_TYPE,F.PARENTOBJECT_ID PARENT_OBJ_ID,B.CREATED_BY,B.CREATE_DATE,B.UPDATED_BY,B.UPDATE_DATE,
B.RECORD_STATUS,B.MONTH,B.YEAR,B.DB_IP_ADDRESS,B.SOURCE_OFFICE_ID,B.TARGET_OFFICE_ID,B.SCREEN_ID
    FROM SGTROLEUSERREL_M A,SGTROLEOBJECTREL_M B,  SGTOBJECT_M C,SGTOBJECTTYPE_M
D,SGTPRIVILEGE_M E,SGTOBJECTREL_M F             WHERE D.ID=C.OBJECTTYPE_ID
AND F.OBJECT_ID = B.OBJECT_ID AND C.ID = B.OBJECT_ID  AND E.ID=B.PRIVILEGE_ID
AND B.ROLE_ID = A.ROLE_ID  AND A.RECORD_STATUS='ACTIVE' AND C.RECORD_STATUS='ACTIVE'
AND E.RECORD_STATUS='ACTIVE' AND D.RECORD_STATUS='ACTIVE'  AND B.RECORD_STATUS='ACTIVE'
AND F.RECORD_STATUS='ACTIVE'  WITH NO ROW MOVEMENT ;
CREATE VIEW CENTRAL_ALL.RBPVDATA      ( USER_ID, ROLE_ID, OBJECT_ID,  OBJECT_NAME,
PRIV_NAME,      OBJ_TYPE, PARENT_OBJ_ID, CREATED_BY, CREATE_DATE,  UPDATED_BY,
     UPDATE_DATE, RECORD_STATUS, MONTH, YEAR, DB_IP_ADDRESS,       SOURCE_OFFICE_ID,
TARGET_OFFICE_ID, SCREEN_ID )  AS  SELECT A.USER_ID  USER_ID,A.ROLE_ID
ROLE_ID,C.ID AS OBJECT_ID,  C.NAME AS OBJECT_NAME,E.NAME  AS PRIVILEGE_NAME,
 D.TYPE OBJ_TYPE,F.PARENTOBJECT_ID PARENT_OBJ_ID,B.CREATED_BY,B.CREATE_DATE,B.UPDATED_BY,B.UPDATE_DATE,
 B.RECORD_STATUS,B.MONTH,B.YEAR,B.DB_IP_ADDRESS,B.SOURCE_OFFICE_ID,B.TARGET_OFFICE_ID,B.SCREEN_ID
         FROM SGTROLEUSERREL_M A,SGTROLEOBJECTREL_M B,  SGTOBJECT_M C,SGTOBJECTTYPE_M
 D,SGTPRIVILEGE_M E,SGTOBJECTREL_M F                 WHERE D.ID=C.OBJECTTYPE_ID
 AND F.OBJECT_ID = B.OBJECT_ID AND C.ID = B.OBJECT_ID  AND E.ID=B.PRIVILEGE_ID
 AND B.ROLE_ID = A.ROLE_ID  AND A.RECORD_STATUS='ACTIVE' AND C.RECORD_STATUS='ACTIVE'
 AND E.RECORD_STATUS='ACTIVE' AND D.RECORD_STATUS='ACTIVE'  AND B.RECORD_STATUS='ACTIVE'
 AND F.RECORD_STATUS='ACTIVE'  WITH NO ROW MOVEMENT ;

